import jsonimport datetimeVAULT_FILE = "spark_vault.json"def add_spark(title, author, category):    try:        with open(VAULT_FILE, "r") as f:            data = json.load(f)    except:        data = []    idea = {        "idea_id": len(data) + 1,        "title": title,        "author": author,        "category": category,        "status": "Proposed",        "date": str(datetime.datetime.now())    }    data.append(idea)    with open(VAULT_FILE, "w") as f:        json.dump(data, f, indent=4)    print("ðŸ’¡ Spark Registered Successfully")if __name__ == "__main__":    title = input("Idea Title: ")    author = input("Author (Balaji/Partner/System): ")    category = input("Category: ")    add_spark(title, author, category)