import pandas as pdfrom datetime import datetimeclass PerformanceExporter:    def __init__(self, portfolio):        self.portfolio = portfolio    def export_weekly(self):        data = {            "Sharpe": self.portfolio.get_sharpe(),            "Drawdown": self.portfolio.get_drawdown(),            "Win Rate": self.portfolio.get_win_rate(),            "Volatility": self.portfolio.get_volatility(),            "Total Return": self.portfolio.get_total_return(),            "Trades": self.portfolio.get_trade_count(),        }        df = pd.DataFrame([data])        filename = f"weekly_report_{datetime.now().date()}.xlsx"        df.to_excel(filename, index=False)        print("Weekly report exported:", filename)