import jsonimport randomVAULT_FILE = "spark_vault.json"def score_idea(idea):    # Basic scoring model (can evolve later)    impact = random.randint(5, 10)    complexity = random.randint(3, 8)    stability = 10 - complexity    score = impact * 0.5 + stability * 0.5    return {        "impact_score": impact,        "complexity_score": complexity,        "stability_score": stability,        "final_score": round(score, 2)    }def evaluate_all():    with open(VAULT_FILE, "r") as f:        data = json.load(f)    for idea in data:        if idea.get("status") == "Proposed":            scores = score_idea(idea)            idea.update(scores)            idea["status"] = "Evaluated"    with open(VAULT_FILE, "w") as f:        json.dump(data, f, indent=4)    print("ðŸ§  Spark Evaluation Completed")if __name__ == "__main__":    evaluate_all()