import pandas as pdimport jsonVAULT_FILE = "spark_vault.json"def import_excel(file_path):    df = pd.read_excel(file_path)    with open(VAULT_FILE, "r") as f:        data = json.load(f)    for _, row in df.iterrows():        data.append({            "idea_id": len(data)+1,            "title": row["title"],            "author": row["author"],            "category": row["category"],            "status": "Proposed"        })    with open(VAULT_FILE, "w") as f:        json.dump(data, f, indent=4)    print("ðŸ“¥ Excel Ideas Imported")