import pandas as pdimport numpy as npfrom core.backtest_engine_v2 import BacktestEngineV2, BacktestConfig# Dummy strategy for testingclass TestStrategy:    def generate_signals(self, df):        df["signal"] = 0                # Simple EMA crossover        df["ema_fast"] = df["close"].ewm(span=10).mean()        df["ema_slow"] = df["close"].ewm(span=20).mean()        df.loc[df["ema_fast"] > df["ema_slow"], "signal"] = 1        df.loc[df["ema_fast"] < df["ema_slow"], "signal"] = -1                return df# Generate fake price data (Saturday safe test)np.random.seed(42)prices = np.cumsum(np.random.randn(500)) + 100df = pd.DataFrame({    "close": prices})config = BacktestConfig(    initial_capital=100000,    mode="fixed")engine = BacktestEngineV2(TestStrategy(), config)report = engine.run(df)print("\n===== BACKTEST REPORT =====")for k, v in report.items():    print(f"{k}: {v}")